mapper.select:
SELECT
  ttr.id as record_id, ttr.agent_id, ttr.trade_count, ttr.trade_amount, ttr.agent_percent as trade_agent_percent, ttr.agent_amount,
  ttr.creator_id, ttr.gmt_created, ttr.gmt_modified, tai.real_name as agent_name, tai.mobile_no, tai.merchant_id
from tb_trade_record ttr
inner join tb_agent_info tai on tai.id=ttr.agent_id
where 1=1

    and tai.parent_agent_id=#{parentAgentId, jdbcType=INTEGER}


    and tai.id=#{agentId, jdbcType=INTEGER}


    and tai.merchant_id=#{merchantId, jdbcType=VARCHAR}


    and tai.real_name=#{realName, jdbcType=VARCHAR}


    and ttr.gmt_created >=#{beginDate, jdbcType=TIMESTAMP}
    and ttr.gmt_created <=#{endDate, jdbcType=TIMESTAMP}

order by ttr.gmt_created desc
